let n = 1
let command = "LOAD " + n + " "; // Base command

if (msg.payload.On !== undefined) {
    // If "On" key exists, turn on or off based on its value
    command += msg.payload.On ? "100" : "0";
} else if (msg.payload.Brightness !== undefined) {
    // If "Brightness" key exists, set brightness level
    command += msg.payload.Brightness.toString();
}
command += "\r"
msg.payload = command;
return msg;

// Initialize or get the current position
let currentPosition = context.get('currentPosition') || 0;
let targetPosition = msg.payload.TargetPosition;

// Prepare the command for the device
let deviceCommand = "#0.0.0.A=" + targetPosition + "\r";

// Update context only if target position is different from current position
if (currentPosition !== targetPosition) {
    context.set('currentPosition', targetPosition); // Update the current position in context

    // Send the command to the device
    node.send([{ payload: deviceCommand }, null]);

    // Note: The "CurrentPosition" should only be updated after the device confirms the position has changed
    // Present simulated this with a delay
    setTimeout(() => {
        let homekitMessage = {
            payload: {
                "CurrentPosition": targetPosition,
                "TargetPosition": targetPosition,
                "PositionState": 2 // Stopped
            }
        };
        // Send the updated state to HomeKit
        node.send([null, homekitMessage]);
    }, 100); // Delay for 100msec to simulate device movement time
}

