// Base command
let command = "LOAD " + msg.payload.light_num + " ";

if (msg.payload.On !== undefined) 
    command += msg.payload.On ? "100" : "0";

else if (msg.payload.Brightness !== undefined) 
    command += msg.payload.Brightness.toString();

msg.payload = command + "\r";
return msg;

const levelToThresholdMap = {
    "DN": 0, "G3": 25, "G2": 50, "G1": 75, "UP": 100
};

function getNearestLevel(percentage) {
    const levels = Object.keys(levelToThresholdMap);
    let nearestLevel = levels[0];
    let nearestDifference = Math.abs(percentage - levelToThresholdMap[nearestLevel]);
    for (let level of levels) {
        let threshold = levelToThresholdMap[level];
        let difference = Math.abs(percentage - threshold);
        if (difference < nearestDifference) {
            nearestDifference = difference;
            nearestLevel = level;
        }
    }
    return nearestLevel;
}

function controlBlind(blinds_num, targetPosition){
    let currentPosition = context.get('currentPosition');
    // node.warn('target position: ' + targetPosition + ' \n' + 'current position: ' + currentPosition);
    let sendCmdFeedback = currentPosition != targetPosition;
    let targetLevel = getNearestLevel(targetPosition);

    targetPosition = levelToThresholdMap[targetLevel];
    context.set('currentPosition', targetPosition);

    let deviceCommand;
    if(blinds_num == 0){
        deviceCommand = "#0.0.0.B=" + targetLevel + "\r";
    } else {
        deviceCommand = "#255."+blinds_num+".255.B=" + targetLevel + "\r";
    }

    if (sendCmdFeedback) {
        node.send([{ payload: deviceCommand }, null]);
        setTimeout(() => {
            let homekitMessage = {
                "CurrentPosition": targetPosition,
                "TargetPosition": targetPosition
            };

            let feedback = {
                "BlindsNum": blinds_num,
                "HomeKitMessage": homekitMessage
            };
            node.send([null, { payload: feedback }]);
        }, 1000);
    } else {
        currentPosition = context.set('currentPosition', -1);
    }
}

let blinds_number = msg.payload.blinds_num;
let targetposition = msg.payload.TargetPosition;
controlBlind(blinds_number, targetposition);

let numOfOutputs = 21;
let blindsNum = msg.payload.BlindsNum;
let homekitMessage = msg.payload.HomeKitMessage;
let outputIndex;

// Determine the output index based on the blindsNum
if(blindsNum == 0){
    outputIndex = 0;
} else if (blindsNum >= 2 && blindsNum <= 4) {
    outputIndex = 22 - blindsNum;
} else if (blindsNum >= 8 && blindsNum <= 24) {
    outputIndex = 25 - blindsNum;
} else {
    outputIndex = null;
}

// output array with nulls for all except the one determined by the blindsNum
let outputArray = Array.from({ length: numOfOutputs }, () => null);
if (outputIndex !== null) {
    outputArray[outputIndex] = { payload: homekitMessage };
}

return outputArray;

let partial_cmd = flow.get("cmd");

let full_cmd = partial_cmd + msg.payload + "\r";

return { payload: full_cmd };

let G = msg.payload;
let partial_cmd;

if (G === '0') {
    partial_cmd = "#0.0.0.B=";
} else if (G) {
    partial_cmd = "#255." + G + ".255.B=";
}

flow.set("cmd", partial_cmd);

// Extract the hexadecimal value after "255.QY="
const match = msg.payload.match(/.*\.255\.QY=([0-9a-fA-F]{2})/);

if (match) {
    const hexVal = match[1];
    let decVal = parseInt(hexVal, 16);
    decVal = Math.floor((decVal*100)/255);
    msg.payload = "Blind level: " + decVal.toString() + "% closed";
    return msg;
} else {
    return msg;
}

let light_val = msg.payload; 
let light_num = msg.light_num;

msg.payload = "LOAD " + light_num + " " + light_val + "\r";

return msg;

let light_num = '';

// Array to hold all the patterns and their corresponding processing functions
const patternHandlers = [
    { pattern: /R:GETLOAD ([0-9]+) ([0-9]+)/, handler: processPattern1 },
    { pattern: /R:ECHO:(.+)/, handler: processPattern2 },
    { pattern: /R:VERSION (.+)/, handler: processPattern3 }
];

function updateLightVal(light_val){
    if (light_val === "0") {
        light_val = "OFF";
    } else if (light_val === "100") {
        light_val = "ON";
    } else {
        light_val += "%";
    }
    return light_val;
}

// Function to process the first pattern
function processPattern1(match) {
    light_num = match[1];
    let light_val = match[2];
    let lightObj = flow.get("lightObj") || {};

    switch(light_num){
        case '1': 
        case '2': 
            lightObj[light_num] = updateLightVal(light_val);
            flow.set("lightObj", lightObj); break;

        case '3': lightObj = flow.get("lightObj");
            msg.payload = 
            `<p>Light1: ${lightObj["1"]}</p>
             <p>Light2: ${lightObj["2"]}</p>
             <p>Light3: ${updateLightVal(light_val)}</p>`; break;
    }
}

// Echo command response
function processPattern2(match) {
    msg.payload = "ECHO: " + match[1];
}

// Version command response
function processPattern3(match) {
    msg.payload = "WattStopper LMDI Version: " + match[1];
}

for (let i = 0; i < patternHandlers.length; i++) {
    const { pattern, handler } = patternHandlers[i];
    const match = msg.payload.match(pattern);
    if (match) {
        handler(match);
        if((i===0 && light_num==='3') || (i>0)){
            return msg;
        }
    }
}

switch(msg.payload)
{
    case "CLEAN 1&2&3 100":
      msg.payload = "Clean mode: All lights ON"; break; 

    case "CLEANCANCEL 1&2&3":
      msg.payload = "Clean cancel: All lights OFF"; break; 

    case "UNLOCKBUTTONS":
      msg.payload = "Wall mounted switches unlocked"; break; 

    case "LOCKBUTTONS 0":
      msg.payload = "Wall mounted switches locked"; break; 

    case "SETSCENE 1":
      msg.payload = "Current scene saved"; break; 

    case "SCENE 1":
      msg.payload = "Preset scene recalled"; break; 

    default:
      msg.payload = "Cmd not configured yet!";  
}

return msg;
